function doGet(e){

  var ss = SpreadsheetApp.openByUrl("https://docs.google.com/spreadsheets/d/1zp1Jl0-SFp2iPhXelwXlL7w0Gc4WZEWj4d9crAuon8s/edit#gid=0");
  var sheet = ss.getSheetByName("Sheet1");
  return insert(e,sheet);


}

function doPost(e){
  var ss = SpreadsheetApp.openByUrl("https://docs.google.com/spreadsheets/d/1zp1Jl0-SFp2iPhXelwXlL7w0Gc4WZEWj4d9crAuon8s/edit#gid=0");
  var sheet = ss.getSheetByName("Sheet1");
  return insert(e,sheet);

}

function insert(e,sheet) {

  var scannedData = e.parameter.sdata;
 var d = new Date();
  var ctime =  d.toLocaleString();
  var roll=scannedData.split("o");

  sheet.appendRow([roll[0],"1",roll[1]]);

  return ContentService
  .createTextOutput(scannedData)
  .setMimeType(ContentService.MimeType.JAVASCRIPT);  



}
